import s from './bascet.module.css';

import { useEffect, useState } from 'react';

import { database } from '../../../firebase/firebase';

import { useSelector } from "react-redux";
import { getUserToken } from '../../../store/user';


const Bascet = () => {


    const userId = useSelector(getUserToken);

    const [data, setData] = useState({});

    const [array, setArr] = useState([]);

    console.log(userId);

    useEffect(() => {
        getData();
    })




    const getData = () => {
        if (Object.keys(userId).length != 0) {
            database.ref('/users/' + localStorage.getItem("idToken") + '/basket/').once('value', (snapshot) => {
                const db = snapshot.val();
                if (db != null) {
                    setData(Object.entries(db));
                }
            });


        } else {
            if (JSON.parse(localStorage.getItem("data2")) != null) {

                setData(JSON.parse(localStorage.getItem("data2")));
            }
        }
    }



    const check = (id) => {
        let checked = false;
        if (document.getElementById(id).checked === true) {
            document.getElementById(id).checked = checked;
            setArr(array.filter(item => item !== id));

        } else if (document.getElementById(id).checked === false) {
            document.getElementById(id).checked = !checked;
            setArr([id].concat(array));
        }

    }

    console.log(array);

    const del = () => {

        if (Object.keys(userId).length == 0) {
            for (let i = 0; i <= array.length - 1; i++) {
                let items = JSON.parse(localStorage.getItem("data2"));
                items = items.filter((item) => item[1].id !== array[i]);
                localStorage.setItem("data2", JSON.stringify(items));
            }



        } else {

            for (let i = 0; i <= array.length - 1; i++) {
                database.ref('/users/' + userId + '/basket/' + array[i]).remove();
            }



        }

        window.location.reload();

    }

    console.log(array);
    console.log(data);

    return (

        <div className={s.container}>

            {
                Object.values(data).map(([key, { name, id, image, species, status }]) =>
                    <div className={s.wrapper} onClick={() => {

                        check(id);

                    }
                    }>

                        <div className={s.checkbox}>
                            <input type="checkbox" id={id} />
                        </div>

                        <div className={s.text}>
                            <p>{name}</p>
                            <p>{status}</p>
                            <p>{species}</p>
                            <h1>{id}</h1>
                        </div>
                        <div className={s.img}>
                            <img src={image}></img>
                        </div>
                    </div>
                )
            }

            <div className={s.buy}>
                <h1>Купить сейчас</h1>
                <button>Buy</button>
                <button onClick={() => {

                    del();

                }}>Clear</button>
            </div>


        </div>

    );
}

export default Bascet;
