import React, { useEffect, useState } from "react";

import s from './HeaderBanner.module.scss';
import cn from "classnames";

import rick from './../img/rick.png';
import summer from './../img/summer.png';
import jerry from './../img/jerry.png';
import beth from './../img/beth.png';

const HeaderBanner = () => {


    const img = [
        <img key={rick} src={rick} />,
        <img key={summer} src={summer} />,
        <img key={jerry} src={jerry} />,
        <img key={beth} src={beth} />,
    ];

    // Индекс текущего слайда
    const [activeIndex, setActiveIndex] = useState(0);

    // Хук Effect
    useEffect(() => {
        // Запускаем интервал
        const interval = setInterval(() => {
            // Меняем состояние
            setActiveIndex((current) => {
                // Вычисляем индекс следующего слайда, который должен вывестись
                const res = current === img.length - 1 ? 0 : current + 1
                // Возвращаем индекс
                return res
            })
        }, 3000)
        // Выключаем интервал
        return () => clearInterval()
    }, [])

    // Вычисляем индекс предыдущего слайда
    const prevImgIndex = activeIndex ? activeIndex - 1 : img.length - 1
    // Вычисляем индекс следующего слайда
    const nextImgIndex = activeIndex === img.length - 1 ? 0 : activeIndex + 1

    // let img = [
    //     {
    //         image: rick,
    //         name: "Rick"
    //     },
    //     {
    //         image: summer,
    //         name: "summer"
    //     },
    //     {
    //         image: jerry,
    //         name: "jerry"
    //     },
    //     {
    //         image: beth,
    //         name: "beth"
    //     },
    // ];

    // let [index, setIndex] = useState(0);

    // const [show, setShow] = useState(img[index].image);

    // const nextImg = () => {
    //     if (index < img.length - 1) {
    //         setIndex(index + 1);
    //         setShow(img[index + 1].image);
    //     } else if (index = img.length - 1) {
    //         setIndex(0);
    //         setShow(img[0].image);
    //     }
    // }


    // setTimeout(() => { nextImg() }, 5000);


    return (
        <div className="slider">
            <div className={cn(s.slider_img, s.slider_img_prev)}
                key={prevImgIndex}>
                {img[prevImgIndex]}
            </div>
            <div className={s.slider_img}
                key={activeIndex}>
                {img[activeIndex]}
            </div>
            <div className={cn(s.slider_img, s.slider_img_next)}
                key={nextImgIndex}>
                {img[nextImgIndex]}
            </div>
        </div>
    );
}

export default HeaderBanner;
