import React, { useState, useEffect } from "react";
import { database } from "../../../firebase/firebase";


import s from './HeaderBanner.module.css';


import rick from './../img/rick.png';
import pickleRick from './../img/pickleRick.png';
import summer from './../img/summer.png';
import jerry from './../img/jerry.png';
import beth from './../img/beth.png';



const HeaderBanner = () => {

    const getData = () => {

        database.ref('hot').once('value', (snapshot) => {
            const data = snapshot.val();
            setChar(data);
        });

    }

    useEffect(() => {
        setTimeout(() => {
            getData();
        }, 1000);
    }, []);

    const [isLoading, setLoading] = useState(false);
    const [char, setChar] = useState({});
    let [index, setIndex] = useState(0);
    const [show, setShow] = useState({});

    console.log(isLoading);

    const loaded = () => {
        if (isLoading == true) {
            nextImg();
        }
    }

    const nextImg = () => {
        if (index < Object.keys(char).length - 1) {
            setShow(char[index]);
            setIndex(++index);
        } else if (index = Object.keys(char).length - 1) {
            setShow(char[index]);
            setIndex(0);
        }
    }





    return (
        <>

            {
                Object.entries(char).map(([key, { name, img, description }]) =>
                    <div className={s.wrap} style={{ backgroundImage: "url(" + img + ")" }}>
                        <div className={s.character}>
                            <h1>{name}</h1>
                            <p>{description}</p>
                        </div>
                        <button className={s.buttons} onClick={() => { console.log(img[index]); }}>Show more</button>
                    </div>
                )
            }
        </>
    );
}

export default HeaderBanner;
