import './App.css';
import Header from './components/header/Header';
import HeaderBanner from './components/header/HeaderBaner/HeaderBanner';
import Bascet from './components/main/bascet/bascet';
// import HomePage from './components/HomePage';
import RegisterPage from './components/main/LoginForm/forms/reg';

import HeaderMobile from './mobile/header/Header';
import HomePageMobile from './mobile/HomePage/HomePageMobile';
import CatalogMobile from './mobile/catalog/catalogMobile';
import BasketMobile from './mobile/basket/basket';

import { BrowserView, MobileView, isBrowser, isMobile } from 'react-device-detect';

import { Route, Switch } from "react-router-dom";

import { useDispatch } from 'react-redux';
import { isUserLoad, token } from './store/user';
import React, { Suspense, useEffect, useState } from 'react';
import User from './components/main/user/User';

import FirebaseClass from './firebase/getData';

const HomePage = React.lazy(() => import('./components/HomePage'));


function App() {

  const [notNull, setNull] = useState();
  const [userToken, setToken] = useState({});

  useEffect(() => {

    // FirebaseClass.getCharacters();

    if (localStorage.getItem("idToken") != null) {
      setNull(true);
      setToken(localStorage.getItem("idToken"));
    } else {
      setNull(false);
    }
  }, []);

  const dispatch = useDispatch();

  dispatch(isUserLoad(notNull));
  dispatch(token(userToken));


  return (
    <>
      <BrowserView>
        <Suspense fallback={<RegisterPage />}>
          <Switch>
            <Route>
              <>
                <Header />
                <Switch>
                  <Route path="/" exact component={HeaderBanner} />
                  <Route path="/characters" render={() => <HomePage />} />
                  <Route path="/basket" component={Bascet} />
                  <Route path="/reg" component={RegisterPage} />
                  <Route path="/userpage" component={User} />
                </Switch>
              </>
            </Route>
          </Switch>
        </Suspense>
      </BrowserView>
      <MobileView>
        <Switch>
          <Route>
            <>
              <HeaderMobile />
              <Switch>
                <Route path="/" exact component={HomePageMobile} />
                <Route path="/characters" component={CatalogMobile} />
                <Route path="/basket" component={BasketMobile} />
                <Route path="/reg" component={RegisterPage} />
                <Route path="/userpage" component={User} />
              </Switch>
            </>
          </Route>
        </Switch>
      </MobileView>
    </>









  );
}

export default App;
