import React, { useEffect, useState } from "react";
import { database } from "../../../firebase/firebase";

import s from './HeaderBanner.module.css';


import rick from './../img/rick.png';
import pickleRick from './../img/pickleRick.png';
import summer from './../img/summer.png';
import jerry from './../img/jerry.png';
import beth from './../img/beth.png';



const HeaderBanner = () => {

    useEffect(() => {
        getData();
    }, []);

    const [char, setChar] = useState([]);

    const getData = () => {
        database.ref('characters').orderByChild("id").limitToFirst(5).once('value', (snapshot) => {
            const data = snapshot.val();
            setChar(data);
        });
    }



    console.log(char);
    console.log(typeof char);


    let img = [
        {
            image: rick,
            name: "Rick"
        },
        {
            image: pickleRick,
            name: "pickleRick"
        },
        {
            image: summer,
            name: "summer"
        },
        {
            image: jerry,
            name: "jerry"
        },
        {
            image: beth,
            name: "beth"
        },
    ];
    // let img = {
    //     0: bg,
    //     1: bg2,
    //     2: bg3,
    //     3: bg4,
    // }

console.log(img.length);

    let [index, setIndex] = useState(0);

    const [show, setShow] = useState(img[index].image);

    const nextImg = () => {
        if (index < img.length - 1) {
            setIndex(index + 1);
            setShow(img[index]);
        } else if (index = img.length - 1) {
            setShow(img[index]);
            setIndex(0);
        }
    }

    setTimeout(() => { nextImg() }, 10000);


    return (
        <div className={s.wrap} style={{ backgroundImage: "url(" + show + ")" }}>
            <div className={s.character}>
                <h1>{img[index].name}</h1>
                <p>Jgbcfybt</p>
            </div>
            <button className={s.buttons}>Show more</button>
        </div>
    );
}

export default HeaderBanner;
