import React, { useState, useEffect } from "react";
import { database } from "../../../firebase/firebase";


import s from './HeaderBanner.module.css';


const HeaderBanner = () => {



    const getData = () => {
        database.ref('hot').once('value', (snapshot) => {
            const data = snapshot.val();
            const shuffled = data.sort(() => 0.5 - Math.random());
            let selected = shuffled.slice(0, 1);
            setChar(selected);

        });

    }

    setTimeout(() => {
        getData();
    }, 500);

    const [dataChar, setData] = useState([]);
    const [char, setChar] = useState({});
    const [index, setIndex] = useState(2);
    const [prev, setPrev] = useState(1);
    const [show, setShow] = useState({});

    console.log(dataChar);

    const getChar = () => {
        let selected = dataChar.slice(prev, index);
        setChar(selected);
        if (prev <= Object.keys(dataChar) - 1) {
            setPrev(prev + 1);
            setIndex(index + 1);
        } else {
            setPrev(0);
            setIndex(1);
        }
    }



    return (
        <>

            {
                Object.entries(char).map(([key, { name, img, description }]) =>
                    <div className={s.wrap} style={{ backgroundImage: "url(" + img + ")" }}>
                        <div className={s.character}>
                            <h1>{name}</h1>
                            <p>{description}</p>
                        </div>
                        <button className={s.buttons} onClick={() => { console.log(img[index]); }}>Show more</button>
                    </div>
                )
            }
        </>
    );
}

export default HeaderBanner;
