import s from './bascet.module.css';
import { getData } from '../../../store/selected';
import { useSelector } from 'react-redux';
import { useState, useEffect } from 'react';


const Bascet = () => {


    const [array, setArr] = useState([]);

    const [checked, setCheck] = useState(true);

    let data;
    // let array = [];

    const dd = useSelector(getData);

    if (dd != null) {
        data = dd;
    } else {
        data = {};
    }

    const check = (id) => {
        document.getElementById(id).checked = !checked;
        setCheck(!checked);
    }

    const del = (id) => {

        document.getElementById(id).checked = checked;
        setArr([id].concat(array));

        console.log(array);

        setCheck(!checked);



    }
    console.log(checked);

    console.log(array);

    return (

        <>

            {
                Object.values(data).map(([key, { name, id, image, species, status }]) =>
                    <div className={s.wrapper} onClick={() => {

                        check(id);

                    }
                    }>

                        <div className={s.checkbox}>
                            <input type="checkbox" id={id} />
                        </div>

                        <div className={s.text}>
                            <p>{name}</p>
                            <p>{status}</p>
                            <p>{species}</p>
                            <h1>{id}</h1>
                        </div>
                        <div className={s.img}>
                            <img src={image}></img>
                        </div>
                    </div>
                )
            }

            <div className={s.buy}>
                <h1>Купить сейчас</h1>
                <button>Buy</button>
                <button onClick={() => {
                    localStorage.removeItem("data2");
                    window.location.reload();
                }}>Clear</button>
            </div>


        </>

    );
}

export default Bascet;
