import React, { useState, useEffect } from "react";
import { database } from "../../../firebase/firebase";


import s from './HeaderBanner.module.css';


const HeaderBanner = () => {

    useEffect(() => {
        if (Object.entries(char).length === 0) {
            getChar();
        }
    }, []);

    const getChar = async () => {
        await database.ref('hot').once('value', (snapshot) => {
            const data = snapshot.val();
            setChar(data);
        });
    }

    const [char, setChar] = useState({});
    const [arr, setArr] = useState({});
    const [index, setIndex] = useState(0);

    // setTimeout(() => {
    //     // for (let i = index; i <= Object.entries(char).length - 1; i++) {
    //     let rand = char[Math.floor(Math.random() * 4)];
    //     hhh(rand);
    //     // }
    // }, 4000);




    // const randomChar = async () => {
    //     let gg = await char;
    //     let shuffled = gg.sort(() => 0.5 - Math.random());
    //     let selected = shuffled.slice(0, 1);
    //     setRand(selected);
    // }

    // console.log(rand);

    const hhh = (rand) => {
        if (index > Object.entries(char).length - 1) {
            setIndex(0);
        } else if (index <= Object.entries(char).length - 1) {
            // let shuffled = char.sort(() => 0.5 - Math.random());
            // let selected = shuffled.slice(0, 1);
            console.log(rand);
            console.log(index);
            // console.log(arr);
            setIndex(index + 1);
            // console.log(index);
        }
        setTimeout(hhh, 1000);
    }



    // const [dataChar, setData] = useState([]);
    // const [char, setChar] = useState({});




    return (
        <>

            {

                Object.entries(char).map(([key, { name, img, description }]) =>
                    <div className={s.wrap} style={{ backgroundImage: "url(" + img + ")" }}>
                        <div className={s.character}>
                            <h1>{name}</h1>
                            <p>{description}</p>
                        </div>
                        <button className={s.buttons}>Show more</button>
                    </div>
                )
            }
        </>
    );
}

export default HeaderBanner;
