import { useState } from 'react';
import s from './Information.module.css';

const Information = () => {

  const [edit, setEdit] = useState(true);
  const [email, setEmail] = useState();

  const onCountryChange = (e) => {
    setEmail(e.target.value);
  }

  const onCityChange = (e) => {
    setEmail(e.target.value);
  }

  const onStreetChange = (e) => {
    setEmail(e.target.value);
  }

  const onHouseChange = (e) => {
    setEmail(e.target.value);
  }

  const onPhoneChange = (e) => {
    setEmail(e.target.value);
  }


  return (
    <>
      <div className={s.userInfoHeader}>
        <h1>INFORMATION</h1>
        {edit == true &&
          <button onClick={() => {
            setEdit(!edit);
          }}>Редактировать</button>
        }
        {edit == false &&
          <div>
            <button onClick={() => {
              setEdit(!edit);
            }}>Сохранить</button>
            <button onClick={() => {
              setEdit(!edit);
            }}>Отмена</button>
          </div>
        }
      </div>
      <div className={s.container}>
        <table>
          <tr>
            <td>Страна</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} onChange={onCountryChange} />
            </td>
            <td>Город</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} onChange={onCityChange} />
            </td>
          </tr>
          <tr>
            <td>Улица</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} onChange={onStreetChange} />
            </td>
            <td>Дом</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} onChange={onHouseChange} />
            </td>
          </tr>
          <tr>
            <td colspan="2">Электронная почта</td>
            <td colspan="2">
              gjghjj
            </td>
          </tr>
          <tr>
            <td colspan="2">Номер телефона</td>
            <td colspan="2"><input type="text" value="hghgh" disabled={edit} onChange={onPhoneChange} /></td>
          </tr>
        </table>
      </div>
    </>
  );
}

export default Information;
