import { useState } from 'react';
import s from './Header.module.css';

const HeaderMobile = () => {
    const [kk, setKk] = useState("none");

    const openModal = () => {
        document.getElementById('id01').style.display = 'block'
    }

    const closeModal = () => {
        setKk("block");
    }


    return (
        <>
            <h1>Модальном окно</h1>

            <button id="myBtn" onClick={openModal()}>Открыть модальном окно</button>

            <div id="myModal" className={s.modal} style = {display={kk}}>

                <div class="modal-content">
                    <span class="close">&times;</span>
                    <p>Какой-то текст в модальном..</p>
                </div>

            </div>
        </>
    );
}

export default HeaderMobile;
