import { useEffect, useState } from 'react';

import { useSelector } from "react-redux";
import { getUserToken } from '../../../store/user';

import { database } from '../../../firebase/firebase';
import s from './User.module.css';

import Orders from './orders/Orders';
import Information from './information/Information';

const User = () => {

  const [orderArr, setOrderArr] = useState([]);

  const [country, setCountry] = useState();
  const [city, setCity] = useState();
  const [street, setStreet] = useState();
  const [house, setHouse] = useState();
  const [phone, setPhone] = useState();

  const [isClicked, setClicked] = useState();

  const [userName, setUserName] = useState("username");
  const userId = useSelector(getUserToken);

  useEffect(() => {
    getDataName();
    getDataInfo();
    getDataOrders();
  }, [])

  const getDataName = () => {
    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/name/').once('value', (snapshot) => {
        const db = snapshot.val();
        setUserName(db.name);
      });
    }
  }

  const getDataInfo = () => {

    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/information/').once('value', (snapshot) => {
        const db = snapshot.val();
        if (db != null) {
          setCountry(db.country);
          setCity(db.city);
          setStreet(db.street);
          setHouse(db.house);
          setPhone(db.phone);
        }
      });

    }
  }

  const getDataOrders = () => {
    let huer = [];
    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/orders/').once('value', (snapshot) => {
        const db = snapshot.val();
        let ar = Object.entries(db);
        ar.forEach ((element) => {
          
          setOrderArr([element[1]].concat(orderArr));
          console.log(orderArr);
          // huer.push(element[1])
        })

        // })
        // console.log(Object.entries(db)[1][1]);
        // console.log(Object.keys(db).length);
        // setOrderArr(db);
        // for (let i = 0; i <= Object.keys(db).length - 1; i++) {
        //   let ar = Object.entries(db);
        //   console.log(ar);
          // Array.prototype.push.apply(huer, Object.entries(db)[i][1]);
          // console.log(Object.entries(db)[i][1]);
          // [Object.entries(db)[i][1]].concat(huer)
          // huer.push(Object.entries(db)[i][1]);
          // setOrderArr(i);
        // }

        });

    }
    // setOrderArr(huer);
    console.log(huer);

  }

  return (
    <>
      <div className={s.container}>
        <div className={s.userInfo}>
          <h1>{userName}</h1>
        </div>
        <div className={s.userNavBar}>
          <button onClick={() => { setClicked("orders") }}>Заказы</button>
          <button onClick={() => { setClicked("information") }}>Information</button>
        </div>
        <div>
          {isClicked == "orders" &&
            <Orders
            // name={itemName}
            // price={price}
            />
          }

          {isClicked == "information" &&
            <Information
              userCountry={country}
              userCity={city}
              userStreet={street}
              userHouse={house}
              userPhone={phone}
            />
          }
        </div>
      </div>
    </>
  );
}

export default User;
