import { useEffect, useState } from 'react';

import { useSelector } from "react-redux";
import { getUserToken } from '../../../store/user';

import { database } from '../../../firebase/firebase';
import s from './User.module.css';

import Orders from './orders/Orders';
import Information from './information/Information';
import Preloader from '../preloader/preloader';

const User = () => {

  const [orderArr, setOrderArr] = useState({});
  const [orderNum, setOrderNum] = useState({});

  const [country, setCountry] = useState();
  const [city, setCity] = useState();
  const [street, setStreet] = useState();
  const [house, setHouse] = useState();
  const [phone, setPhone] = useState();

  const [isClicked, setClicked] = useState();

  const [userName, setUserName] = useState("username");
  const userId = useSelector(getUserToken);

  const [isLoading, setLoading] = useState(false);

  useEffect(() => {
    getDataName();
    getDataInfo();
    getDataOrders();
  }, [])

  const getDataName = () => {
    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/name/').once('value', (snapshot) => {
        const db = snapshot.val();
        setUserName(db.name);
        setLoading(true);
      });
    }
  }

  const getDataInfo = () => {

    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/information/').once('value', (snapshot) => {
        const db = snapshot.val();
        if (db != null) {
          setCountry(db.country);
          setCity(db.city);
          setStreet(db.street);
          setHouse(db.house);
          setPhone(db.phone);
          setLoading(true);
        }
      });

    }
  }

  const getDataOrders = () => {
    let huer = [];
    let nums = [];
    if (Object.keys(userId).length != 0) {
      database.ref('/users/' + userId + '/orders/').once('value', (snapshot) => {
        const db = snapshot.val();
        console.log(db);
        let ar = Object.entries(db);
        huer.push(ar);


        console.log(ar);
        // ar.forEach((element) => {
        //   console.log(element[0]);
        //   nums.push(element[0]);
        //   huer.push(element[0]);
        //   console.log(element[1]);
        //   Object.entries(element[1]).forEach((el) => {
        //     huer.push(el[1]);
        //   })
        //   huer.push(element[1])
        // })
      });
    }
    console.log(huer);
    setOrderArr(huer);
    // setOrderNum(nums);
    setLoading(true);
  }

  return (
    <>
      {
        (isLoading === true) &&
        <div className={s.container}>
          <div className={s.userInfo}>
            <h1>{userName}</h1>
          </div>
          <div className={s.userNavBar}>
            <button onClick={() => { setClicked("orders") }}>Заказы</button>
            <button onClick={() => { setClicked("information") }}>Information</button>
          </div>
          <div>
            {isClicked == "orders" &&


              // Object.entries(orderArr).map(([key, { name, price, process }]) =>
                <Orders
                  orderArr={orderArr}
                />
              // )
            }

            {isClicked == "information" &&
              <Information
                userCountry={country}
                userCity={city}
                userStreet={street}
                userHouse={house}
                userPhone={phone}
              />
            }
          </div>
        </div>
      }

      {(isLoading === false) &&
        <div>
          <Preloader />
        </div>
      }

    </>
  );
}

export default User;
