import { useState } from 'react';
import s from './Information.module.css';

const Information = () => {

  const [edit, setEdit] = useState(true);


  return (
    <>
      <div className={s.userInfoHeader}>
        <h1>INFORMATION</h1>
        {edit == true &&
          <button onClick={() => {
            setEdit(!edit);
          }}>Редактировать</button>
        }
        {edit == false &&
          <button onClick={() => {
            setEdit(!edit);
          }}>Сохранить</button> &&
          <button onClick={() => {
            setEdit(!edit);
          }}>Сохранить</button>
        }
      </div>
      <div className={s.container}>
        <table>
          <tr>
            <td>Страна</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} />
            </td>
            <td>Город</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} />
            </td>
          </tr>
          <tr>
            <td>Улица</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} />
            </td>
            <td>Дом</td>
            <td>
              <input type="text" value="hghgh" disabled={edit} />
            </td>
          </tr>
          <tr>
            <td colspan="2">Электронная почта</td>
            <td colspan="2"><input type="text" value="hghgh" disabled={edit} /></td>
          </tr>
          <tr>
            <td colspan="2">Номер телефона</td>
            <td colspan="2"><input type="text" value="hghgh" disabled={edit} /></td>
          </tr>
        </table>
      </div>
    </>
  );
}

export default Information;
