import React from "react";
import {
    Link
} from "react-router-dom";

import { getUserData } from "../../store/user";

import { ReactComponent as BagSVG } from './img/bag.svg';
import { ReactComponent as LogoSVG } from './img/logo1.svg'

import s from './Header.module.scss';
import { useSelector } from "react-redux";

import LoadTrue from "./loadTRUE";
import LoadFalse from "./loadFALSE";

const Header = () => {

    const loadStatus = useSelector(getUserData);

    return (

            <div className={s.container}>
                <div className={s.logo}>
                    <Link to="/">
                        <LogoSVG />
                    </Link>
                </div>
                {/* <div className={s.menu}>
                    <nav>
                        <ul>
                            <li>
                                <Link to="/characters">Characters</Link>
                            </li>
                        </ul>
                    </nav>
                </div> */}
                <div className={s.user}>
                    <nav>
                        <ul>
                        <li>
                                <Link to="/characters">Characters</Link>
                            </li>
                            <li>
                                {loadStatus == false &&
                                    <LoadFalse />
                                }

                                {loadStatus == true &&
                                    <LoadTrue />
                                }
                            </li>
                            <li>
                                <Link to="/basket" className={s.bagWrap}>
                                    <BagSVG />
                                </Link>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>

    );
}

export default Header;
