import { createSlice } from "@reduxjs/toolkit";

export const slice = createSlice({
    name: 'card',
    initialState: {
        isLoading: false,
        data: {},
        error: null,
    },
    reducers: {
        getCards: state => ({
            ...state,
            isLoading: true,
        }),
        getCardsResolve: (state, action) => ({
            ...state,
            isLoading: false,
            data: state.push(action.payload),
        }),
        getCardsReject: (state, action) => ({
            ...state,
            isLoading: false,
            data2: {},
            error: action.payload,
        }),
    }

});

export const { getCards, getCardsResolve, getCardsReject} = slice.actions;

export const getData = state => state.card.data;



export default slice.reducer;
