import s from './catalog.module.css';
import { database } from '../../firebase/firebase';
import { getDatabase, ref, query, limitToLast } from "firebase/database";
import React, { useState, useEffect } from "react";
import Card from '../card/card';

import { useDispatch, useSelector } from 'react-redux';
import { getCards, getCardsResolve } from '../../store/selected';

import FirebaseClass from '../../firebase/firebase';



const Catalog = () => {

  const dispatch = useDispatch();


  const [char, setChar] = useState([]);
  const [clicked, setClicked] = useState(JSON.parse(localStorage.getItem("data2")));

  const [arr, setArr] = useState([]);

  // const [start, setStart] = useState(1);
  const [last, setLast] = useState();

  dispatch(getCardsResolve(clicked));


  useEffect(() => {
    // FirebaseClass.getCharacters();

    // getDataKey();
    getData();
  }, []);

  const getDataKey = () => {
    database.ref('characters').orderByChild("id").once('value', (snapshot) => {
      snapshot.forEach((childSnapshot) => {
        let childKey = childSnapshot.key;
        console.log(childKey);
        setArr(arr => [...arr, childKey]);
      });

    });
  }

  const getData = () => {
    // database.ref('characters').limitToFirst(start).once('value', (snapshot) => {
    database.ref('characters').orderByChild("id").startAt(1).limitToFirst(8).once('value', (snapshot) => {
      snapshot.forEach((childSnapshot) => {
        let childKey = childSnapshot.key;
        setArr(arr => [...arr, childKey]);
      });
      const data = snapshot.val();
      //random characters
      // const shuffled = data.sort(() => 0.5 - Math.random());
      // let selected = shuffled.slice(0, 8);
      setChar(data);

    });


  }

  console.log(arr);

  // const nexPage = () => {
  //   let num =  parseInt(arr[arr.length - 1]);
  //   console.log(typeof num);
  //   getData(num);
  //   setLast(parseInt(arr[0]))
  //   setArr([]);
  // }

  // const prevPage = () => {

  //   getData(last);
  // }

  const buy = (id) => {
    let obj = Object.entries(char);
    const keyPock = obj.find(element =>
      element[1].id === id);


    const clicks = JSON.parse(localStorage.getItem("data2")) ?? []
    clicks.push(keyPock);
    localStorage.setItem("data2", JSON.stringify(clicks));
    setClicked(JSON.parse(localStorage.getItem("data2")));
    // console.log();
  };



  return (
    <>
      {/* <div className={s.wrap} onWheel={nexPage}> */}
      <div className={s.wrap}>
        <div className={s.app}>
          {
            Object.entries(char).map(([key, { name, id, image, species, status }]) =>
              <Card
                key={id}
                name={name}
                image={image}
                id={id}
                species={species}
                status={status}

                onCardClick={() => {

                  buy(id);

                }
                }
              />
            )
          }
        </div>
        {/* <button onClick={nexPage}>next</button>
        <button onClick={prevPage}>prev</button> */}
      </div>
    </>


  );
}

export default Catalog;
