import s from './bascet.module.css';
import { getData } from '../../../store/selected';
import { useSelector } from 'react-redux';
import { useState } from 'react';


const Bascet = () => {

    const [checked, setCheck] = useState(true);

    let data;
    let array = [];

    const dd = useSelector(getData);

    if (dd != null) {
        data = dd;
    } else {
        data = {};
    }

    const del = (id) => {
        document.getElementById(id).checked = checked;
        setCheck(!checked);
        if (document.getElementById(id).checked = true) {
            array.push(id);
        } else if (document.getElementById(id).checked = false) {
            let i = array.indexOf(id);
            if (i >= 0) {
                array.splice(i, 1);
            }
        }
    }



    return (

        <>

            {
                Object.values(data).map(([key, { name, id, image, species, status }]) =>
                    <div className={s.wrapper} onClick={() => {

                        del(id);

                    }
                    }>

                        <div className={s.checkbox}>
                            <input type="checkbox" id={id} />
                        </div>

                        <div className={s.text}>
                            <p>{name}</p>
                            <p>{status}</p>
                            <p>{species}</p>
                            <h1>{id}</h1>
                        </div>
                        <div className={s.img}>
                            <img src={image}></img>
                        </div>
                    </div>
                )
            }

            <div className={s.buy}>
                <h1>Купить сейчас</h1>
                <button>Buy</button>
                <button onClick={() => {
                    localStorage.removeItem("data2");
                    window.location.reload();
                }}>Clear</button>
            </div>


        </>

    );
}

export default Bascet;
