import React, { useState } from "react";

import s from './HeaderBanner.module.css';
import cn from 'classnames';

// var slides = document.querySelectorAll('#slides .slide');
// var currentSlide = 0;
// var slideInterval = setInterval(nextSlide, 2000);

// function nextSlide() {
//     currentSlide = (currentSlide + 1) % slides.length;
//     console.log(currentSlide);
// }


const HeaderBanner = () => {

    const [show, setShow] = useState();

    // let slides = document.getElementById('slides').getElementsByTagName('li').length;
    // console.log(slides);



    const slideShow = true;

    return (
        <div className={s.wrap}>
            <ul id='slides'>
                <li className={cn(s.slide,
                    { [s.showing]: slideShow === false })}>Slide 1</li>
                <li className={cn(s.slide,
                    { [s.showing]: slideShow === true })}>Slide 2</li>
                <li className={s.slide}>Slide 3</li>
                <li className={s.slide}>Slide 4</li>
                <li className={s.slide}>Slide 5</li>
            </ul>
        </div>
    );
}

export default HeaderBanner;
