import s from './catalog.module.css';
import { database } from '../../firebase/firebase';
import { ref, query, startAt } from "firebase/database";
import React, { useState, useEffect } from "react";



const Catalog = () => {
  const [char, setChar] = useState({});



  useEffect(() => {

    const recentPostsRef = query(ref(database, 'pocharactersts'), startAt(100));
    setChar(recentPostsRef);

    // database.ref('characters').once('value', (snapshot) => {
    //   const data = snapshot.val();
    //   setChar(data);
    //   // const shuffled = data.sort(() => 0.5 - Math.random());
    //   // let selected = shuffled.slice(0, 4);
    //   // setChar(selected);
    // });
  }, []);




  return (
    <>
      <div className={s.app}>
        {
          Object.entries(char).map(([key, { name, image, species, status }]) =>
            <div className={s.block}>
              <a><img src={image}></img></a>
              <div className={s.description}>
                <div className={s.name}>
                  <p>{name}</p>
                </div>
                <hr />
                <p>{species}</p>
                <p>{status}</p>
              </div>
              <div className={s.price}>
                <p>${Math.floor((Math.random() * 10000) + 1)}</p>
                <input type="button" value="Buy" />
              </div>
            </div>
          )
        }

        <button>вперед</button>
        <button>назад</button>

      </div>
    </>
  );
}

export default Catalog;
