import React, { useState, useEffect } from "react";
import { database } from "../../../firebase/firebase";


import s from './HeaderBanner.module.css';


const HeaderBanner = () => {

    useEffect(() => {
        database.ref('hot').once('value', (snapshot) => {
            const data = snapshot.val();
            setChar(data);
        });
    }, []);

    const [char, setChar] = useState({});
    const [rand, setRand] = useState({});



    const randomChar = () => {
        const shuffled = char.sort(() => 0.5 - Math.random());
        let selected = shuffled.slice(0, 1);
        setRand(selected);
    }

    console.log(rand);

    // const hhh = () => {
    //     const shuffled = dataChar.sort(() => 0.5 - Math.random());
    //     let selected = shuffled.slice(0, 1);
    //     setChar(selected);
    // }

    // setTimeout(() => {
    //     randomChar();
    // }, 1000);

    // const [dataChar, setData] = useState([]);
    // const [char, setChar] = useState({});




    return (
        <>

            {
                Object.entries(char).map(([key, { name, img, description }]) =>
                    <div className={s.wrap} style={{ backgroundImage: "url(" + img + ")" }}>
                        <div className={s.character}>
                            <h1>{name}</h1>
                            <p>{description}</p>
                        </div>
                        <button className={s.buttons} onClick={randomChar()}>Show more</button>
                    </div>
                )
            }
        </>
    );
}

export default HeaderBanner;
