import { useEffect, useState } from 'react';
import s from './Information.module.css';

import { database } from '../../../../firebase/firebase';

import { useSelector } from "react-redux";
import { getUserToken } from '../../../../store/user';

const Information = () => {

  const userId = useSelector(getUserToken);

  const [edit, setEdit] = useState(true);

  const [country, setCountry] = useState();
  const [city, setCity] = useState();
  const [street, setStreet] = useState();
  const [house, setHouse] = useState();
  const [phone, setPhone] = useState();

  useEffect(() => {
    getData();
    console.log("gbcmrf");
  })

  const getData = () => {
    database.ref('/users/' + userId + '/information/').once('value', (snapshot) => {
      const db = snapshot.val();
      if (db != null) {
        setCountry(db.country);
        setCity(db.city);
        setStreet(db.street);
        setHouse(db.house);
        setPhone(db.phone);
      }
    });
  }

  const onCountryChange = (e) => {
    setCountry(e.target.value);
  }

  const onCityChange = (e) => {
    setCity(e.target.value);
  }

  const onStreetChange = (e) => {
    setStreet(e.target.value);
  }

  const onHouseChange = (e) => {
    setHouse(e.target.value);
  }

  const onPhoneChange = (e) => {
    setPhone(e.target.value);
  }


  const editUser = () => {
    database.ref('/users/' + userId + '/information/').set(
      {
        country: country,
        city: city,
        street: street,
        house: house,
        phone: phone,
      });
  }

  return (
    <>
      <div className={s.userInfoHeader}>
        <h1>INFORMATION</h1>
        {edit == true &&
          <button onClick={() => {
            setEdit(!edit);
          }}>Редактировать</button>
        }
        {edit == false &&
          <div>
            <button onClick={() => {
              setEdit(!edit);
              editUser();
            }}>Сохранить</button>
            <button onClick={() => {
              setEdit(!edit);
            }}>Отмена</button>
          </div>
        }
      </div>
      <div className={s.container}>
        <table>
          <tr>
            <td>Страна</td>
            <td>
              <input type="text" value={country} disabled={edit} onChange={onCountryChange} />
            </td>
            <td>Город</td>
            <td>
              <input type="text" value={city} disabled={edit} onChange={onCityChange} />
            </td>
          </tr>
          <tr>
            <td>Улица</td>
            <td>
              <input type="text" value={street} disabled={edit} onChange={onStreetChange} />
            </td>
            <td>Дом</td>
            <td>
              <input type="text" value={house} disabled={edit} onChange={onHouseChange} />
            </td>
          </tr>
          <tr>
            <td colspan="2">Электронная почта</td>
            <td colspan="2">
              {localStorage.getItem("email")}
            </td>
          </tr>
          <tr>
            <td colspan="2">Номер телефона</td>
            <td colspan="2"><input type="text" value={phone} disabled={edit} onChange={onPhoneChange} /></td>
          </tr>
        </table>
      </div>
    </>
  );
}

export default Information;
