    onCardClick && onCardClick(id);
}

  return (
    <>
      <div className={s.app}>
        {
          Object.entries(char).map(([key, { name, id, image, species, status }]) =>
            <div className={s.block}>
              <a><img src={image}></img></a>
              <div className={s.description}>
                <div className={s.name}>
                  <p>{name}</p>
                  <p>{id}</p>
                </div>
                <hr />
                <p>{species}</p>
                <p>{status}</p>
              </div>

              <div className={s.buy}>
                <h1>${Math.floor((Math.random() * 10000) + 1)}</h1>
                <button onClick = {onClick}>Buy</button>
              </div>
            </div>
          )
        }

        <button>вперед</button>
        <button>назад</button>

      </div>
    </>
  );
}

export default Card;
