import React, { useState } from "react";

import s from './HeaderBanner.module.css';

import bg from './../img/banner.jpg';
import bg2 from './../img/banner2.jpg';
import bg3 from './../img/banner3.png';
import bg4 from './../img/banner4.jpg';



const HeaderBanner = () => {

    // let img = [bg, bg2, bg3, bg4];
    let img = {
        0: bg,
        1: bg2,
        2: bg3,
        3: bg4,
    }

    let [index, setIndex] = useState(0);

    const [show, setShow] = useState(img[index]);
    console.log(img[3]);

    const nextImg = () => {
        if (index < img.length - 1) {
            setIndex(index + 1);
            setShow(img[index]);
        } else if (index = img.length - 1) {
            setShow(img[index]);
            setIndex(0);
        }
    }

    setTimeout(() => { nextImg() }, 2000);


    return (
        <div className={s.wrap} style={{ backgroundImage: "url(" + show + ")" }}>
            <button className={s.buttons} onClick={() => { console.log(img[index]); }}>Show more</button>
        </div>
    );
}

export default HeaderBanner;
