import s from './bascet.module.css';
import { getData } from '../../../store/selected';
import { useSelector } from 'react-redux';
import { useState } from 'react';


const Bascet = () => {
    let data;

    const dd = useSelector(getData);

    if (dd != null) {
        data = dd;
    } else {
        data = {};
    }

    const del = (id) => {
