import './App.css';
import Header from './components/header/Header';
import HeaderBanner from './components/header/HeaderBaner/HeaderBanner';
import Bascet from './components/main/bascet/bascet';
import HomePage from './components/HomePage';
import RegisterPage from './components/main/LoginForm/forms/reg';

import { Route, Switch } from "react-router-dom";

import { useDispatch } from 'react-redux';
import { isUserLoad, token } from './store/user';
import { useEffect, useState } from 'react';
import UserPage from './components/main/user/User';

import FirebaseClass from './firebase/firebase';


function App() {

  const [notNull, setNull] = useState();
  const [userToken, setToken] = useState({});

  useEffect(() => {



    if (localStorage.getItem("idToken") != null) {

      setNull(true);
      setToken(localStorage.getItem("idToken"));
      const hh = FirebaseClass.getUserName(localStorage.getItem("idToken"));

      console.log(hh);
      // console.log(typeof localStorage.getItem("idToken"));
    } else {
      setNull(false);
    }
  }, []);

  const dispatch = useDispatch();

  dispatch(isUserLoad(notNull));
  dispatch(token(userToken));


  return (

    <Switch>
      <Route>
        <>
          <Header />
          <Switch>
            <Route path="/" exact component={HeaderBanner} />
            <Route path="/characters" exact component={HomePage} />
            <Route path="/basket" component={Bascet} />
            <Route path="/reg" component={RegisterPage} />
            <Route path="/userpage" component={UserPage} />
          </Switch>
        </>
      </Route>
    </Switch>






  );
}

export default App;
