import { createSlice } from "@reduxjs/toolkit";

export const slice = createSlice({
    name: 'user',
    initialState: {
        isLoading: false,
        data: JSON.parse(localStorage.getItem("idToken")),
        error: null,
    },
    reducers: {
        getUser: state => ({
            ...state,
            isLoading: true,
        }),
        isUserLoad: (state, action) => ({
            ...state,
            isLoading: true,
            data: action.payload,
        })
    }

});

export const { getUser, isUserLoad} = slice.actions;

export const getUserData = state => state.user.data;



export default slice.reducer;
