import { createSlice } from "@reduxjs/toolkit";

export const slice = createSlice({
    name: 'user',
    initialState: {
        isLoading: false,
        data: {},
    },
    reducers: {
        fetchUser: (state, action) => ({
            isLoading: true,
            isUserLoad: action.payload,
        }),
    }
});

export const { fetchUser } = slice.actions;

export default slice.reducer;
