import s from './bascet.module.css';
import { getData } from '../../../store/selected';
import { useSelector } from 'react-redux';
import { useState } from 'react';


const Bascet = () => {

    const [checked, setCheck] = useState(false);

    let data;

    const dd = useSelector(getData);

    if (dd != null) {
        data = dd;
    } else {
        data = {};
    }

    const del = (id) => {
        console.log(id);
        document.getElementById(id).checked = !checked;
    }

    // const del = (id) => {
    //     let obj = Object.entries(char);
    //     const keyPock = obj.find(element =>
    //         element[1].id === id);


    //     const clicks = JSON.parse(localStorage.getItem("data2")) ?? []
    //     clicks.push(keyPock);
    //     localStorage.setItem("data2", JSON.stringify(clicks));
    //     setClicked(JSON.parse(localStorage.getItem("data2")));
    //     // console.log();
    // };

    return (

        <>

            {
                Object.values(data).map(([key, { name, id, image, species, status }]) =>
                    <div className={s.wrapper} onClick={() => {

                        del(id);

                    }
                    }>

                        <div className={s.checkbox}>
                            <input type="checkbox" id={id} />
                        </div>

                        <div className={s.text}>
                            <p>{name}</p>
                            <p>{status}</p>
                            <p>{species}</p>
                            <h1>{id}</h1>
                        </div>
                        <div className={s.img}>
                            <img src={image}></img>
                        </div>
                    </div>
                )
            }

            <div className={s.buy}>
                <h1>Купить сейчас</h1>
                <button>Buy</button>
                <button onClick={() => {
                    localStorage.removeItem("data2");
                    window.location.reload();
                }}>Clear</button>
            </div>


        </>

    );
}

export default Bascet;
