import s from './catalog.module.css';
import { database } from '../../firebase/firebase';
import React, { useState, useEffect } from "react";
import Card from '../card/card';

import { useDispatch, useSelector } from 'react-redux';
import { getCards, getCardsResolve } from '../../store/selected';



const Catalog = () => {

  const dispatch = useDispatch();


  const [char, setChar] = useState([]);
  const [clicked, setClicked] = useState({});

  dispatch(getCardsResolve(clicked));


  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    database.ref('characters').once('value', (snapshot) => {
      const data = snapshot.val();
      const shuffled = data.sort(() => 0.5 - Math.random());
      let selected = shuffled.slice(0, 8);
      setChar(selected);
    });

  }

  const buy = (id) => {
    let obj = Object.entries(char);
    const keyPock = obj.find(element =>
      element[1].id === id);
    setClicked(keyPock);

      const clicks = JSON.parse(localStorage.getItem("data2")) ?? [{}]
      clicks.push(clicked);
      localStorage.setItem("data2", JSON.stringify(clicks));
    };




  return (
    <>
      <div className={s.app}>
        {
          Object.entries(char).map(([key, { name, id, image, species, status }]) =>
            <Card
              key={id}
              name={name}
              image={image}
              id={id}
              species={species}
              status={status}

              onCardClick={() => {

                buy(id);

              }
              }
            />
          )
        }

        <button>вперед</button>
        <button>назад</button>

      </div>
    </>



