import s from './catalog.module.css';
import { database } from '../../firebase/firebase';
import React, { useState, useEffect } from "react";

// import { useDispatch, useSelector } from 'react-redux';
// import { getCards } from '../../store/selected';



const Catalog = () => {

// const dispatch = useDispatch();


  const [char, setChar] = useState([]);

  const [page, setPage] = useState(1);

  const[selected2, setSelected] = useState({});

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    database.ref('characters').once('value', (snapshot) => {
      const data = snapshot.val();
      const shuffled = data.sort(() => 0.5 - Math.random());
      let selected = shuffled.slice(0, 8);
      setChar(selected);
    });

  }

  console.log(selected2);

  return (
    <>
      <div className={s.app}>
        {
          Object.entries(char).map(([key, { name, image, species, status }]) =>
            <div className={s.block}>
              <a><img src={image}></img></a>
              <div className={s.description}>
                <div className={s.name}>
                  <p>{name}</p>
                </div>
                <hr />
                <p>{species}</p>
                <p>{status}</p>
              </div>
              <div className={s.price}>
                <p>${Math.floor((Math.random() * 10000) + 1)}</p>
                <input type="button" value="Buy" onClick={setSelected(char[key])}/>
              </div>
            </div>
          )
        }

        <button>вперед</button>
        <button>назад</button>

      </div>
    </>
  );
}

export default Catalog;
